import React from "react";
import Button from "@mui/material/Button";
import HeroDesign from "../assets/HeroDesign.png";
import { Outlet, Link } from "react-router-dom";
import hero from "../assets/hero.jpg";
function Summaryorder() {
  return (
    <div style={{ marginTop: "50px" }}>
      <h1>Your order</h1>{" "}
      <Link
        to={"/details"}
        className="btn btn-primary"
        aria-current="page"
        style={{
          fontWeight: "bold",
          color: "black",
          marginRight: "100px",
          marginLeft: "10px",
        }}
      >
        Great I'm hungry
      </Link>
      <Link
        to={"/order"}
        className="btn btn-danger"
        aria-current="page"
        style={{
          fontWeight: "bold",
          color: "black",
          marginRight: "50px",
        }}
      >
        Back to Menu
      </Link>
      <h1>Total price : price</h1>
      <div className="container">

      <div class="card">
            <img
              src="https://img.taste.com.au/YsjGYwdM/taste/2018/09/layered-thai-noodle-salad-142001-1.jpg"
              class="card-img-top"
              alt="..."
            />
            <div class="card-body">
              <h3 class="card-title">Salad Smash</h3>
              <span style={{ width: "100px", height: "100px" }}>üçÉ</span>
              <p class="card-text">
                lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum
                lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum
                lorem ipsum lorem
              </p>
            </div>
            <div class="card-footer">
              <small
                style={{
                  color: "red",
                  fontSize: "bold",
                }}
              >
                8$
              </small>
              <Button>üõí</Button>
            </div>
          </div>
          <div class="card">
            <img src={hero} class="card-img-top" alt="..." />
            <div class="card-body">
              <h3 class="card-title">Cheese Burger Delux </h3>
              <p class="card-text">Garlic, cucember, The best nut cream</p>
            </div>
            <div class="card-footer">
              <small
                style={{
                  color: "red",
                  fontSize: "bold",
                }}
              >
                15$
              </small>
              <Button>üõí</Button>
            </div>
          </div>
      </div>
    </div>
  );
}

export default Summaryorder;
