import React, { useState } from "react";
import Button from "@mui/material/Button";
import HeroDesign from "../assets/HeroDesign.png";
import { Outlet, Link } from "react-router-dom";
import hero from "../assets/hero.jpg";
function Summaryorder() {
  const [order, setOrder] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/v1/orders/")
      .then((res) => res.json())
      .then((data) => setOrder(data));
  }, []);

  return (
    <div style={{ marginTop: "50px", marginBottom: "100px" }}>
      <h1>Your order</h1>{" "}
      <Link
        to={"/details"}
        className="btn btn-primary"
        aria-current="page"
        style={{
          fontWeight: "bold",
          color: "black",
          marginRight: "100px",
          marginLeft: "10px",
        }}
      >
        Great I'm hungry
      </Link>
      <Link
        to={"/"}
        className="btn btn-danger"
        aria-current="page"
        style={{
          fontWeight: "bold",
          color: "black",
          marginRight: "50px",
        }}
      >
        Back to Menu
      </Link>
      <h1>Total price : price</h1>
      <div className="container">
        <div class="row">
          <div className="">
            <div class="card">
              <img
                src="https://img.taste.com.au/YsjGYwdM/taste/2018/09/layered-thai-noodle-salad-142001-1.jpg"
                class="card-img-top"
                alt="..."
              />
              <div class="card-body">
                <h3 class="card-title">Salad Smash</h3>

                <span style={{ width: "100px", height: "100px" }}>üçÉ</span>
                <p class="card-text">
                  lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum
                  lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum
                  lorem ipsum lorem
                </p>
              </div>
              <div class="card-footer">
                <small
                  style={{
                    color: "red",
                    fontSize: "bold",
                  }}
                >
                  8$
                </small>
                <Button>‚ùå</Button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Summaryorder;
