import React from "react";

function EmptyCart() {
  return (
    <div>
      <div class="alert alert-danger d-flex align-items-center" role="alert">
        <svg
          class="bi flex-shrink-0 me-2"
          width="24"
          height="24"
          role="img"
          aria-label="Danger:"
        >
          
        </svg>
        <div>Your cart is empty, Please add something to your cart</div>
      </div>
    </div>
  );
}

export default EmptyCart;
