import Button from "@mui/material/Button";
import HeroDesign from "../assets/HeroDesign.png";
import { Outlet, Link } from "react-router-dom";
import React, { useState, useEffect } from "react";

function Final({ cart }) {
  const [sum, setSum] = useState(0);
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/v1/orders/")
      .then((res) => res.json())
      .then((data) => setOrders(data));
  }, []);

  useEffect(() => {
    let sumCart = 0;
    cart.map((dish) => {
      sumCart += dish.price;
      setSum(sumCart);
    });
  }, []);

  return (
    <div>
      {orders.length((order) => {
        <div
          style={{
            position: "absolute",
            textAlign: "center",
            top: "20%",
            left: "40%",
          }}
        >
          <img src={HeroDesign} alt="" />
          <h2
            style={{
              position: "absolute",
              textAlign: "center",
              top: "30%",
              left: "25%",
            }}
          >
            Thank You !
          </h2>
          <h3 style={{ marginTop: "50px" }}>Your order ID : {order.id}</h3>
          <h5>Total price: {sum}</h5>
          <h6 style={{ marginTop: "5px" }}>
            Time order created : {order.time}
          </h6>
          <Link to={"/"}>
            <Button
              variant="contained"
              color="success"
              style={{
                position: "absolute",
                textAlign: "center",
                top: "46%",
                left: "28%",
              }}
            >
              Return to{" "}
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                class="bi bi-house-door-fill"
                viewBox="0 0 16 16"
              >
                <path d="M6.5 14.5v-3.505c0-.245.25-.495.5-.495h2c.25 0 .5.25.5.5v3.5a.5.5 0 0 0 .5.5h4a.5.5 0 0 0 .5-.5v-7a.5.5 0 0 0-.146-.354L13 5.793V2.5a.5.5 0 0 0-.5-.5h-1a.5.5 0 0 0-.5.5v1.293L8.354 1.146a.5.5 0 0 0-.708 0l-6 6A.5.5 0 0 0 1.5 7.5v7a.5.5 0 0 0 .5.5h4a.5.5 0 0 0 .5-.5z" />
              </svg>
            </Button>
          </Link>
        </div>;
      })}
    </div>
  );
}

export default Final;
