import React, { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import HeroDesign from "../assets/HeroDesign.png";
import { Outlet, Link } from "react-router-dom";
import hero from "../assets/hero.jpg";
function Summaryorder({ cart, setCart }) {
  const [sum, setSum] = useState(0);

  useEffect(() => {
    // fetch("http://127.0.0.1:8000/api/v1/orders/")
    //   .then((res) => res.json())
    //   .then((data) => setOrders(data));

    let sumCart = 0;
    cart.map((dish) => {
      sumCart += dish.price;
      setSum(sumCart);
    });
  }, []);

  return (
    <div style={{ marginTop: "50px", marginBottom: "100px" }}>
      <h1>Your order</h1>{" "}
      <Link
        to={"/details"}
        className="btn btn-primary"
        aria-current="page"
        style={{
          fontWeight: "bold",
          color: "black",
          marginRight: "100px",
          marginLeft: "10px",
        }}
      >
        Great I'm hungry
      </Link>
      <Link
        to={"/order"}
        className="btn btn-danger"
        aria-current="page"
        style={{
          fontWeight: "bold",
          color: "black",
          marginRight: "50px",
        }}
      >
        Back to Menu
      </Link>
      <h1>Total price : {sum}$</h1>
      <div className="container">
        <div className="row row-cols-5 row-cols-md-2 g-4">
          {cart.map((dish) => (
            <div key={dish.id}>
              <div className="col">
                <div class="card">
                  <img src={dish.imageUrl} class="card-img-top" alt="..." />
                  <div class="card-body">
                    <h3 class="card-title">{dish.name}</h3>

                    <span style={{ width: "100px", height: "100px" }}>üçÉ</span>
                    <p class="card-text">{dish.description}</p>
                  </div>
                  <div class="card-footer">
                    <small
                      style={{
                        color: "red",
                        fontSize: "bold",
                      }}
                    >
                      {dish.price} $
                    </small>
                    <Button onClick={() => setCart("delete", dish)}>‚ùå</Button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Summaryorder;
