import React, { useState, useEffect } from "react";
import hero from "../assets/hero.jpg";
import salads from "../assets/salads.jpg";
import HeroDesign from "../assets/HeroDesign.png";
import FoodYummy from "../assets/FoodYummy.png";
import product1 from "../assets/product1.jpg";
import product2 from "../assets/product2.jpg";
import product3 from "../assets/product3.jpg";
import product4 from "../assets/product4.jpg";
import drinks from "../assets/drinks.jpg";
import Stack from "@mui/material/Stack";
import Button from "@mui/material/Button";
import { Outlet, Link } from "react-router-dom";
import { color } from "@mui/system";
function Order({ cart, setCart }) {
  const [dishes, setDishes] = useState([]);
  const [categories, setCategories] = useState([]);
  const [filteredDishes, setFilteredDishes] = useState(dishes);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/v1/dish/")
      .then((res) => res.json())
      .then((data) => setDishes(data));
  }, []);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/v1/category/")
      .then((res) => res.json())
      .then((data) => setCategories(data));
  }, []);

  const filteredDishesByCategory = (category_id) => {
    fetch(`http://127.0.0.1:8000/api/v1/dish/?category=${category_id}`)
      .then((res) => res.json())
      .then((data) => setFilteredDishes(data));
  };

  return (
    <div>
      <div
        className="container"
        style={{
          backgroundColor: "#ffffff",
          opacity: "0.9",
          marginBottom: "50px",
          marginTop: "10px",
        }}
      >
        <div
          class="card bg-light text-white"
          style={{ fontWeight: "bold", color: "#000000" }}
        >
          <nav class="navbar navbar-expand-lg navbar-light bg-light">
            <div class="container-fluid">
              {categories.map((category) => (
                <button
                  onClick={() => filteredDishesByCategory(category.id)}
                  class="navbar-brand"
                >
                  <img
                    src={category.imageUrl}
                    style={{ width: "100px", height: "100px" }}
                    alt="Icon"
                  />
                  {category.name}
                </button>
              ))}
              <div class="collapse navbar-collapse" id="navbarTogglerDemo03">
                <ul class="navbar-nav me-auto mb-2 mb-lg-0">
                  <li class="nav-item" style={{ marginLeft: "50px" }}>
                    <Link to={"/"} class="btn btn-warning" aria-current="page">
                      Go back ‚Ü∫
                    </Link>
                  </li>
                  <li class="nav-item" style={{ marginLeft: "50px" }}>
                    <Link
                      to={"/summary"}
                      class="btn btn-warning"
                      aria-current="page"
                    >
                      My üõí {cart.length}
                    </Link>
                  </li>
                  <li class="nav-item" style={{ marginLeft: "50px" }}>
                    <Link to={"/"} class="btn btn-warning" aria-current="page">
                      Best deals üí∞
                    </Link>
                  </li>
                  <li class="nav-item" style={{ marginLeft: "50px" }}>
                    <Link to={"/"} class="btn btn-warning" aria-current="page">
                      Rate our food üíï
                    </Link>
                  </li>
                </ul>
              </div>
            </div>
          </nav>
        </div>
      </div>
      <div className="container">
        <div className="row row-cols-3 row-cols-md-3 g-6">
          {filteredDishes.map((dish) => (
            <div key={dish.id} className="col">
              <div class="card" style={{ borderRadius: "20px" }}>
                <img
                  src={dish.imageUrl}
                  class="card-img-top"
                  alt="..."
                  style={{ height: "300px" }}
                />
                <div class="card-body">
                  <h3 class="card-title">{dish.name}</h3>
                  <div>
                    {dish.isVegeterian ? (
                      <span
                        style={{
                          width: "100px",
                          height: "100px",
                          color: "green",
                        }}
                      >
                        üçÉ Veggie
                      </span>
                    ) : (
                      " "
                    )}
                    <br></br>
                    {dish.isGlutenFree ? (
                      <span
                        style={{
                          width: "100px",
                          height: "100px",
                          color: "blue",
                        }}
                      >
                        üçû Gloten free
                      </span>
                    ) : (
                      " "
                    )}
                  </div>
                  <p class="card-text">{dish.description}</p>
                </div>
                <div class="card-footer">
                  <small
                    style={{
                      color: "red",
                      fontSize: "bold",
                    }}
                  >
                    {dish.price} $
                  </small>
                  <Button onClick={() => setCart("add", dish)}>üõí</Button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      <div
        style={{
          position: "relative",
          textAlign: "center",
        }}
      >
        <img
          src={HeroDesign}
          style={{
            position: "relative",
            left: "38%",
          }}
        />
        <h3
          style={{
            position: "relative",
            bottom: "220px",
            textAlign: "center",
            fontFamily: "fantasy",
          }}
        >
          Are you ready to eat?
        </h3>
        <Link to={"/summary"}>
          <Button
            variant="contained"
            color="success"
            style={{
              position: "relative",
              bottom: "180px",
              textAlign: "center",
            }}
          >
            Let's EAT
          </Button>
        </Link>
      </div>
    </div>
  );
}

export default Order;
